import background from '../images/bad-banana-banner.jpeg';
import {
    Card,
    Input,
    Checkbox,
    Button,
    Typography,
} from "@material-tailwind/react";
import {
    MagnifyingGlassIcon,
} from "@heroicons/react/24/outline";

const Header = () => {

    return (
        // create the main hero section
        <section className="relative bg-grey-100"   style={{ backgroundImage: `url(${background})`,}} >
            <div className="max-w-7xl mx-auto px-4 sm:px-6 mt-14 mb-24">
                <div className="pt-32 pb-12 md:pt-40 md:pb-20" > 

                    <div className="mx-auto text-center pb-12 md:pb-10 sm:pb-2 sm:px-2 pt-10 bg-black items-center rounded-md text-white" color='black' >
                        <h1 className="h1">
                            SEARCH A MOVIE
                        </h1>
                        <div className='lg:flex max-w-3xl mx-auto mt-6 gap-1'>
                            <Input size="" label="Name" className='mr-2' />
                            <Button variant="gradient" color="yellow" className="flex items-center gap-2">
                                Search <MagnifyingGlassIcon strokeWidth={2} className="h-4 w-5" />
                            </Button>
                        </div>
                    </div>
                </div>
            </div>
        </section>
    );
}

export default Header;
